mapper.select:
  	select
	count(1)
	from sys_account
	where state=1
	
	and name = #{name,jdbcType=VARCHAR}
	
	
	and phone = #{phone,jdbcType=VARCHAR}
	
	
	and e_mail = #{eMail,jdbcType=VARCHAR}

select

,password
,salt
from sys_account
where state=1

and user_id = #{userId,jdbcType=BIGINT}


