-- ----------------------------
-- Function structure for f_children_category
-- ----------------------------
DROP FUNCTION IF EXISTS `f_children_category`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` FUNCTION `f_children_category`(id BIGINT) RETURNS varchar(21845) CHARSET utf8
BEGIN
DECLARE temp varchar(21845);
DECLARE temp_chd varchar(21845);
SET temp ='$';
SET temp_chd=CAST(id AS char);
WHILE temp_chd IS NOT NULL DO
SET temp=CONCAT(temp,',',temp_chd);
SELECT GROUP_CONCAT(category_id) INTO temp_chd FROM cms_category WHERE FIND_IN_SET(category_p_id,temp_chd)>0;
END WHILE;
RETURN temp;
END
;;
DELIMITER ;
