DROP TABLE IF EXISTS `cms_category`;
CREATE TABLE `cms_category` (
  `category_id` bigint(11) NOT NULL AUTO_INCREMENT COMMENT '栏目ID',
  `site_id` bigint(11) DEFAULT NULL COMMENT '站点ID',
  `category_p_id` bigint(11) DEFAULT NULL COMMENT '栏目父级ID',
  `category_name` varchar(50) DEFAULT NULL COMMENT '栏目名称',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `href` varchar(255) DEFAULT NULL COMMENT '链接',
  `target` varchar(16) DEFAULT NULL COMMENT '跳转类型\r\n            1 普通目录 2 a标签 3 a标签_blank 4 直接加载url信息',
  `keywords` varchar(255) DEFAULT NULL COMMENT '关键字',
  `description` varchar(255) DEFAULT NULL COMMENT '栏目描述',
  `category_order` int(4) DEFAULT NULL COMMENT '排序',
  `state` int(1) DEFAULT NULL COMMENT '状态',
  `create_date` datetime DEFAULT NULL COMMENT '创建时间',
  `update_date` datetime DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`category_id`),
  KEY `FK_CATEGORY_SITE` (`site_id`),
  CONSTRAINT `FK_CATEGORY_SITE` FOREIGN KEY (`site_id`) REFERENCES `cms_site` (`site_id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COMMENT='栏目';

